<template>
	<view class="verification-page">
		<view class="title">
			<text>確保您不是機器人</text>
			<text>請將飛船移動到星球中心</text>
		</view>
		<movable-area class="bg" :style="{backgroundImage: 'url('+bgImage+')'}">
			<movable-view class="move" :x="x" :y="y" direction="all" @change="onChange">
				<image src="../../static/img/login/airship.svg" mode=""></image>
			</movable-view>
			<movable-view class="login-text" x="0" y="300" disabled="true" v-if="isShow">
				<text>您的飛船已著陸,潘多拉星球歡迎您回歸家圊</text>
			</movable-view>
		</movable-area>


	</view>
</template>

<script>
	export default {
		data() {
			return {
				bgImage: "data:image/svg+xml;base64,",
				x: "300",
				y: "550",
				old: {
					x: 0,
					y: 0
				},
				isShow: false,
				flog: true
			}
		},
		methods: {
			onChange: function(e) {
				this.old.x = e.detail.x
				this.old.y = e.detail.y
				if (this.old.x > 90 && this.old.x < 210 && this.old.y < 140 && this.flog) {
					this.flog = false
					this.isShow = true
					var name = uni.getStorageSync("USERS_KEY").name
					var portrait = uni.getStorageSync("USERS_KEY").portrait
					if (!name || !portrait) {
						setTimeout(() => {
							uni.reLaunch({
								url: "../user/update"
							});
						},300)
						return
					}
					setTimeout(() => {
						uni.redirectTo({
							url: '/pages/nearby/tga'
						});
					}, 500)
				}
			}

		}
	}
</script>

